-- load ttf file font. set 20px font-size
mainFont = love.graphics.newFont("anyfont.ttf", 20);

function love.draw() 
	-- set font before draw text
	love.graphics.setFont(mainFont);
	-- draw text "Hello world!" at left: 100, top: 200
	love.graphics.print("Hello world!", 100, 200);
end;

-- carregar arquivo fonte ttf. definir tamanho de fonte de 20px
fontePrincipal = love.graphics.newFont("qualquerfonte.ttf", 20);

function love.draw() 
	-- definir a fonte antes de desenhar o texto
	love.graphics.setFont(fontePrincipal);
	-- desenhar o texto "Alô, mundo!" em esquerda: 100, topo: 200
	love.graphics.print("Alô, mundo!", 100, 200);
end;

function love.load()
	image = love.graphics.newImage("pig.png")

	local vertices = {
		{
			-- top-left corner (red-tinted)
			0, 0, -- position of the vertex
			0, 0, -- texture coordinate at the vertex position
			255, 0, 0, -- color of the vertex
		},
		{
			-- top-right corner (green-tinted)
			image:getWidth(), 0,
			1, 0, -- texture coordinates are in the range of [0, 1]
			0, 255, 0
		},
		{
			-- bottom-right corner (blue-tinted)
			image:getWidth(), image:getHeight(),
			1, 1,
			0, 0, 255
		},
		{
			-- bottom-left corner (yellow-tinted)
			0, image:getHeight(),
			0, 1,
			255, 255, 0
		},
	}

	-- the Mesh DrawMode "fan" works well for 4-vertex Meshes.
	mesh = love.graphics.newMesh(vertices, image, "fan")
end

function love.draw()
	love.graphics.draw(mesh, 0, 0)
end

function love.load()
	imagem = love.graphics.newImage("pig.png")

	local vertices = {
		{
			-- canto superior esquerdo (avermelhado)
			0, 0, -- posição do vértice
			0, 0, -- coordenada de textura na posição do vértice
			255, 0, 0, -- cor do vértice
		},
		{
			-- canto superior direito (esverdeado)
			image:getWidth(), 0,
			1, 0, -- coordenadas de textura estão entre 0 e 1
			0, 255, 0
		},
		{
			-- canto inferior direito (azulado)
			image:getWidth(), image:getHeight(),
			1, 1,
			0, 0, 255
		},
		{
			-- canto inferior esquerdo (amarelado)
			0, image:getHeight(),
			0, 1,
			255, 255, 0
		},
	}

	-- o ModoDeDesenho de Malha "fan" (leque) funciona bem com Malhas de 4 vértices.
	malha = love.graphics.newMesh(vertices, imagem, "fan")
end

function love.draw()
	love.graphics.draw(malha, 0, 0)
end

void effects( vec4 color, Image texture, vec2 texture_coords, vec2 screen_coords )
{
 // love_Canvases is a writable array of vec4 colors. Each index corresponds to a Canvas. If you don't assign a value to all active canvases, bad things might happen.
 love_Canvases[0] = color;
}

void effects( vec4 color, Image texture, vec2 texture_coords, vec2 screen_coords )
{
 // love_Canvases é um array de cores vec4 com permissão de escrita. Cada índice corresponde a uma Pintura. Se você não atribuir um valor para todas pinturas ativas, coisas ruins podem acontecer.
 love_Canvases[0] = color;
}

varying vec4 vpos;

vec4 effect( vec4 color, Image texture, vec2 texture_coords, vec2 screen_coords )
{
 texture_coords += vec2(cos(vpos.x), sin(vpos.y));
 vec4 texcolor = Texel(texture, texture_coords);
 return texcolor * color;
}

